import "./App.css";
import {useState} from "react";
import Axios from 'axios';

function App(){
const [name,setName] = useState("");
const [materialgroup,setMaterialGroup] = useState("");
const [dimension,setDimension] = useState("");
const [quantity,setQuantity] = useState(0);
const [price,setPrice] = useState(0);
const [orderdate,setOrderDate] = useState("");
const [receiptdate,setReceiptDate] = useState("");
const [supplier,setSupplier] = useState("");

const[employeeList, setEmployeeList] = useState([]);

const addEmployee = () => {
    Axios.post('http://localhost:3001/create', {
        name: name,
        materialgroup:materialgroup,
        dimension:dimension,
        quantity:quantity,
        price:price,
        orderdate:orderdate,
        receiptdate:receiptdate,
        supplier:supplier,
    
    }).then(()=> {
        console.log("success");
    });
};

const getEmployees = () => {
    Axios.get('http://localhost:3001/employees').then((response)=> {
        setEmployeeList(response.data)
    });

}

const displayInfo = () => {
    console.log(name,dimension);
}

return (
     <div className="App">
        <div className="information">
            <table class = "table">
                <thead clss="thead-dark">
                    <tr>
                        <th>ID</th>
                        <th>Material group</th>
                        <th>Name</th>
                        <th>Dimension</th>
                        <th>Quantity</th>
                        <th>Price</th>
                        <th>Supplier</th>
                        <th>Order date</th>
                        <th>Receipt date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th></th>
                    </tr>
                </tbody>
            </table>



<label>Material group:</label>
<input type="text"
onChange={(event) => {
    setMaterialGroup(event.target.value);
}}
/>

<label>Name:</label>
<input type="text" 
onChange={(event) => {
    setName(event.target.value);
}}
    />

<label>Dimension:</label>
<input type="text"
onChange={(event) => {
    setDimension(event.target.value);
}}
/>

<label>Quantity:</label>
<input type="number"
onChange={(event) => {
    setQuantity(event.target.value);
}}
/>

<label>Price:</label>
<input type="number"
onChange={(event) => {
    setPrice(event.target.value);
}}
/>

<label>Supplier:</label>
<input type="text"
onChange={(event) => {
    setSupplier(event.target.value);
}}
/>

<label>Set order date:</label>
<input type="date"
onChange={(event) => {
    setOrderDate(event.target.value);
}}
/>

<label>Set receipt date:</label>
<input type="date"
onChange={(event) => {
    setReceiptDate(event.target.value);
}}
/>



<button onClick={addEmployee}>Add Material</button>

<div className="employees">
<button onClick={getEmployees}>Show All Materials</button>
</div>
</div>

{employeeList.map((val,key) => {
    return <div className="table"> 
<thead class="thead-dark">
                    <tr>
                        <th>ID</th>
                        <th>Material group</th>
                        <th>Name</th>
                        <th>Dimension</th>
                        <th>Quantity</th>
                        <th>Price</th>
                        <th>Supplier</th>
                        <th>Order date</th>
                        <th>Receipt date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th>{val.id}</th>
                        <th>{val.materialgroup}</th>
                        <th>{val.name}</th>
                        <th>{val.dimension}</th>
                        <th>{val.quantity}</th>
                        <th>{val.price}</th>
                        <th>{val.orderdate}</th>
                        <th>{val.receiptdate}</th>

                    </tr>
                </tbody>
    </div>
})}
    </div>
)
}

export default App;
